*,
*:before,
*:after {
    padding: 0;
    margin: 0;
    box-sizing: border-box;
    transition: all .2s ease-in-out;
    -webkit-user-drag: none;
    -webkit-user-drag: none;
    -khtml-user-drag: none;
    -moz-user-drag: none;
    -o-user-drag: none;
    user-drag: none;
    background-attachment: none;
}


html,
body {
    overflow-x: hidden;
}

img {
    pointer-events: none;
}

html,
body,
div,
span,
applet,
object,
iframe,
h1,
h2,
h3,
h4,
h5,
h6,
p,
blockquote,
pre,
a,
abbr,
acronym,
address,
big,
cite,
code,
del,
dfn,
em,
img,
ins,
kbd,
q,
s,
samp,
small,
strike,
strong,
sub,
sup,
tt,
var,
b,
u,
i,
center,
dl,
dt,
dd,
ol,
ul,
li,
fieldset,
form,
label,
legend,
table,
caption,
tbody,
tfoot,
thead,
tr,
th,
td,
article,
aside,
canvas,
details,
embed,
figure,
figcaption,
footer,
header,
hgroup,
menu,
nav,
output,
ruby,
section,
summary,
time,
mark,
audio,
video {
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
    font: inherit;
    vertical-align: baseline;
}

/* HTML5 display-role reset for older browsers */
article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
menu,
nav,
section {
    display: block;
}

body {
    line-height: 1;
    font-family: 'Poppins', sans-serif;
}

ol,
ul {
    list-style: none;
}

blockquote,
q {
    quotes: none;
}

blockquote:before,
blockquote:after,
q:before,
q:after {
    content: '';
    content: none;
}

table {
    border-collapse: collapse;
    border-spacing: 0;
}

sources.css\\scss\\components\\_aboutwolf.scss:
.aboutWolf {
    margin-top: 10rem;
    width: 100%;
    background: $clr05;

    &__content {
        @include flexbox(space-between, center);
        @include maxWidth();
        padding: 5rem 0;

        &__right {
            width: 100%;
            height: auto;

            img {
                width: 100%;
                height: auto;
            }
        }

        &__left {
            width: 100%;
            margin-right: 3rem;

            h2 {
                @include aboutwolf-sub();

            }

            h1 {
                @include aboutwolf-heading();
                position: relative;
                line-height: 1.4;

                &:before {
                    content: '';
                    position: absolute;
                    bottom: -25px;
                    left: 0;
                    background: $clr04;
                    width: 25%;
                    height: 4%;
                }
            }

            .link {
                @include button($clr01, #fff, transparent, 3px solid #fff);
            }
        }
    }
}

.aboutWolf:nth-child(1) {
    margin-top: 0;
}

/* Breakpoints */

@media screen and (max-width: 1100px) {

    .main,
    .main-about {
        .aboutWolf {
            &__content {
                padding: 4rem;

            }
        }
    }
}

@media screen and (max-width: 768px) {

    .main,
    .main-about {
        .aboutWolf {
            &__content {
                flex-wrap: wrap-reverse;
                max-width: 100%;
                padding: 6rem 2rem;

                &__left {
                    margin-right: 0;
                    margin-top: 4rem;

                    h2 {
                        font-weight: bold;
                        margin-right: 0;
                    }

                    h1 {
                        font-size: 1.5rem;
                        line-height: 1.4;
                        margin-right: 0;
                    }
                }
            }
        }

        .letsTalk {
            margin-top: 0;

        }
    }
}

