@import "node_modules/@glidejs/glide/src/assets/sass/glide.core";
@import "node_modules/@glidejs/glide/src/assets/sass/glide.theme";

.slider-work {
    width: 100%;
    height: auto;
    text-align: center;

    .heading {
        @include global-heading();
        text-align: center;
        margin: 8rem 0;
    }



    .glide__slide {
        position: relative;

        a:hover>img,
        a:hover>.content {
            filter: blur(1px);
        }


        .content {
            position: absolute;
            top: 50%;
            transform: translateY(-50%);
            left: 2.5%;
            width: 300px;


            h2 {

                font-weight: bold;
                @include slider-heading();
                text-align: left;
            }

            p {

                position: relative;
                @include slider-p();
                text-align: left;

                &:before {
                    content: '';
                    position: absolute;
                    background: $clr02;
                    width: 20%;
                    height: 6px;
                    bottom: -10px;
                    left: 0;
                }
            }
        }
    }

    img {
        max-width: 100%;
        max-height: 100%;
        margin: 0 auto;
        display: block;
        //max-width: auto;
        //max-height: 120px;
    }

.buttons 
{
    margin-top: 3rem; 

    
        .button 
        {
            border: none;
            background:  none;
            color: #282828;
            font-weight: bold;
            padding: 10px 20px;
            font-size: 2.5rem;
            cursor: pointer;
        }
    
}

    .link {
        @include button($clr02, $clr02, $clr02, 3px solid $clr02);
        display: inline-block;

    }
}

/* Breakpoints */

@media screen and(max-width: 768px) {
    .main {
        .slider-work {
            .heading {
                font-size: 3rem;
                margin: 4rem 0;
            }

            .glide__slide {
                .content {

                    h2 {
                        font-size: 2rem;
                        line-height: 1.2;
                    }

                    p {
                        display: none;
                    }
                }



            }

            .glide__arrows {
                .glide__arrow {
                    padding: 10px 20px;
                    font-weight: bold;
                    background: $cs-clr07;
                    opacity: 0.5;

                }
            }
        }
    }


}

