sources.css\\scss\\layout\\_header.scss:
/* Fixed Contact */

.toggle-contact {
    position: fixed;
    right: 0;
    top: 20%;
    padding: 15px;
    background: $contact-bg;
    cursor: pointer;
    color: #fff;
    font-size: 1.5rem;
    font-weight: lighter;
    transition: all 0.01s ease-in-out;
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
    z-index: 25;

    img {
        width: 20px;
        height: auto;
    }

}

.fixed-contact {
    position: fixed;
    transition: all 0.01s ease-in-out;
    right: -100%;
    top: 20%;
    width: 450px;
    height: auto;
    background: $contact-bg;
    padding: 40px 50px;
    z-index: 15;

    #error-message {

        padding: 5px;
        font-weight: lighter;
        font-size: 1.1rem;
        margin-top: 1rem;
        display: flex;
        flex-direction: row;
        line-height: 1.2;
        color: #fff;

    }

    &__container {

        position: relative;



        .heading {
            color: #fff;
            font-size: 1.5rem;
            font-weight: bold;
        }

        &__form {
            margin-top: 2rem;

            .form-control {
                width: 100%;

                label {
                    display: block;
                    color: #fff;
                    font-size: 1.1rem;
                    margin: 2rem 0;
                }

                input {
                    width: 100%;
                    padding: 10px 20px;
                    border: 1px solid #fff;
                    border-radius: 5px;

                    &:focus-within {
                        border: .5px solid $cs-clr01;
                    }
                }

            }

            .btn {
                display: block;
                margin-top: 2rem;
                font-size: 1.1rem;
                font-weight: bold;
                padding: 15px 35px;
                cursor: pointer;
                color: #fff;
                background: $clr05;
                border: none;
                border-radius: 5px;

                &:hover {
                    background: #fff;
                    color: $clr05;

                }
            }
        }

    }


}

/* Toggle class for contact */

.showContact {
    right: 0;
}

.moveToggler {
    right: 450px;
}

.header {
    height: auto;
    padding-bottom: 4rem;
    background: rgb(248, 246, 246);

    &__top-content {
        padding: $padding;
        @include flexbox(space-between, center);
        height: 200px;


        .logo {

            img {
                width: 100px;
                height: auto;
            }
        }

        /* Menu */

        .nav-container {
            position: fixed;
            top: 0;
            left: 0;
            background: $clr06;
            width: 100%;
            opacity: 0;
            visibility: hidden;
            z-index: 90;
            height: 100vh;
            overflow-y: hidden;

            &__container {
                @include maxWidth();
                @include flexbox(space-between, center);
                height: 100%;

                &__menu {
                    .list {
                        list-style: none;
                        opacity: 0;

                        .link {
                            @include linkReset(#fff);
                            font-weight: bold;
                            position: relative;
                            @include menu-link();

                            &:hover {
                                color: $clr03;
                            }
                        }


                        .link:before {
                            content: '';
                            position: absolute;
                            bottom: 0px;
                            width: 0%;
                            height: 2px;
                            background: $clr03;
                            transition: width .5s ease;
                        }

                        .link:hover:before {
                            width: 50%;
                            color: $clr03;
                            transition: width .5s ease;
                        }
                    }

                    .list-home {

                        width: 50px;
                        height: auto;

                        .link-home {
                            .home-ico {
                                width: 25px;
                                height: auto;
                            }
                        }
                    }
                }

                &__contact-details {
                    width: 400px;

                    p {
                        @include menu-description();
                        opacity: 0;
                    }


                    .social {
                        opacity: 0;
                        margin: 2rem 0;
                        @include menu-title();

                        .heading {
                            list-style: none;
                        }

                        .list {
                            padding: 8px 0;
                            list-style: none;

                            .link {
                                @include linkReset(#fff);
                                font-size: 20px;
                                font-weight: normal;

                                &:hover {
                                    color: $clr02;
                                }
                            }
                        }
                    }
                }
            }
        }

        .nav-container::-webkit-scrollbar {
            display: none;
        }

        /* Menu Toggle */
        .toggle-menu {
            background: #f3f7fd;
            width: 50px;
            height: 80px;
            position: fixed;
            right: 0;
            cursor: pointer;
            z-index: 91;

            .bar {
                width: 45%;
                height: 3px;
                background: $clr02;
                position: absolute;
                overflow: hidden;
            }

            .bar-one {
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);
                overflow: hidden;
            }

            .bar-two {
                top: 40%;
                left: 50%;
                width: 45%;
                transform: translate(-50%, -50%);
            }

            .bar-three {
                top: 60%;
                width: 45%;
                left: 50%;
                transform: translate(-50%, -50%);
            }
        }

    }

    /* Showcase */

    .header-showcase {
        @include maxWidth();
        height: 100%;
        @include flexbox(start, start);

        &__content {
            max-width: 50%;

            h2 {
                @include showcase-heading();
                width: 100%;
                position: relative;

                &:before {
                    content: '';
                    position: absolute;
                    left: 0;
                    bottom: -30px;
                    width: 25%;
                    height: 8px;
                    background: $clr03;
                }
            }

            .link {
                @include showcase-link();
                margin-top: 8rem;
                @include flexbox(start, center);
                display: inline-block;
                color: #282828;

                img {
                    width: 28px;
                    height: auto;
                    margin-left: .5rem;
                }

                &:hover img {
                    margin-left: 1rem;
                }
            }
        }
    }


}

/**************************** BREAKPOINTS FOR HEADER **************************/


@media (max-width: 1100px) {
    .header {

        &__top-content {
            .nav-container {
                &__container {

                    flex-wrap: wrap;
                    padding: $padding;

                    &__contact-details {
                        display: none;
                    }
                }
            }
        }

        .header-showcase {
            padding: $padding;

            &__content {
                max-width: 60%;

                h2 {
                    font-size: 45px;
                }
            }
        }
    }
}

@media (max-width: 900px) {
    .header {
        .header-showcase {
            padding: 2rem $padding;

            &__content {
                max-width: 80%;
                margin-top: 0rem;

                h2 {
                    font-size: 45px;

                    &::before {
                        width: 50%;
                        height: 3px;
                    }
                }

                .link {
                    margin-top: 6rem;
                    padding-bottom: 2.5rem;
                }
            }
        }
    }
}


@media (max-width: 768px) {
    .header {
        padding: 0rem 2rem;

        .header-showcase {
            padding: $padding;

            &__content {
                max-width: 100%;
                margin-top: 0rem;

                h2 {
                    font-size: 35px;
                    width: 100%;
                    line-height: 1.4;

                    &::before {
                        width: 50%;
                        height: 3px;
                    }
                }

                .link {
                    margin-top: 5rem;
                }
            }
        }
    }
}

@media (max-width: 500px) {

    .toggle-contact {

        right: 0;
        z-index: 30;
        border: 3px solid #fff;
    }

    .fixed-contact {
        width: 100%;
    }

    .header {

        &__top-content {
            .logo {
                img {
                    width: 80px;
                    height: auto;
                }
            }

            padding: 0;
            margin-top: 0;

            .nav-container {
                &__container {
                    &__menu {
                        .list {

                            .link {
                                font-size: 2rem;
                            }

                        }
                    }

                    flex-wrap: wrap;
                    padding: $padding;

                    &__contact-details {
                        display: none;
                    }
                }
            }
        }

        .header-showcase {
            padding: 0;
            margin-top: 0rem;
        }
    }
}


@media screen and(max-height: 700px) {

    .toggle-contact {
        top: 25%;
    }

    .fixed-contact {
        padding: 5px 20px;
        top: 25%;

        &__container {
            &__form {
                .form-control {
                    label {
                        margin: 1rem;
                    }
                }
            }

            .btn {
                margin: 1rem;
            }
        }
    }

    .header {
        .toggle-menu {
            top: 5%;
        }
    }
}

sources.css\\scss\\pages\\_about-us.scss:
/* Small Resets For Common Pages */

.header-about {
    background: none;
    height: auto;
}

.aboutWolf-about {
    margin-top: 0;
}

/* Showcase Heading */
.header-about {
    .about-showcase {
        background: none;

        h2 {
            @include pack-heading();
            margin: 5rem 0;
        }
    }
}

/* MAIN CONTENT ABOUT US PAGE */
.main-about {
    .team-description {
        background: $clr01;

        .showcase {
            width: 100%;
            height: auto;
            margin-top: -3rem;
        }

        &__content-top {
            text-align: center;
            @include maxWidthInner();
            padding: 6rem 0;

            h2 {
                @include team-sub();
                margin-bottom: 1.5rem;
                position: relative;
                display: inline-block;

                &:before {
                    content: '';
                    position: absolute;
                    left: 50%;
                    transform: translateX(-50%);
                    bottom: -12px;
                    width: 90%;
                    height: 2px;
                    background: $clr02;
                }
            }

            h1 {
                @include team-heading();
                margin-bottom: 1.5rem;
            }

            p {
                @include team-p(lighter);
                margin-bottom: 1.5rem;
            }


            h3 {
                @include team-h3();
            }

        }

        &__content-bottom {
            background: #fff;
            display: grid;
            grid-template-columns: 1fr 1fr;
            grid-template-areas: 'content-one showcase-one''showcase-two content-two';

            &__left-content-one {
                grid-area: content-one;
                padding: $padding;
                @include flexbox(center, start);
                height: 100%;
                flex-direction: column;

                h2 {
                    @include team-desc-heading();
                    margin-bottom: 1.5rem;
                }

                p {
                    @include team-desc-p();
                }
            }

            &__right-showcase-one {
                grid-area: showcase-one;

                img {
                    width: 100%;
                    height: 60vh;
                    display: block;
                }
            }

            &__right-content-two {
                grid-area: content-two;
                padding: $padding;
                @include flexbox(center, start);
                height: 100%;
                flex-direction: column;

                h2 {
                    @include team-desc-heading();
                    margin-bottom: 1.5rem;
                }

                p {
                    @include team-desc-p();
                }
            }

            &__left-showcase-two {
                grid-area: showcase-two;

                img {
                    width: 100%;
                    height: 55vh;
                    display: block;
                }
            }
        }
    }

    /* THE PACK */

    .thePack {
        .heading {
            @include pack-heading();
            margin: 10rem 0;
        }

        &__content {
            img {
                width: 100%;
                height: auto;
                display: block;
                filter: grayscale(100%);

                &:hover {
                    filter: grayscale(0);
                }
            }

            /* flex description & quote */

            &__flex {
                @include flexbox(space-between, center);
                width: 100%;
                height: auto;
                position: relative;
                z-index: 3;
                margin-top: -3rem;

                &__left-quote,
                &__right-desc {
                    width: 50%;
                    @include flexbox(center, center);
                    flex-direction: column;
                    height: 60vh;

                    h2,
                    span,
                    p {
                        width: 50%;
                    }

                    h2 {
                        @include team-quote();
                        padding-bottom: 2rem;
                    }

                    span {
                        @include team-quote-author();
                    }

                    p:nth-child(1) {
                        @include team-quote-p(lighter);
                        margin-bottom: 1rem;
                    }

                    p:nth-child(2) {
                        @include team-quote-p(bold);
                        margin-bottom: 1rem;
                    }
                }

                &__right-desc {
                    background: $clr02;
                }
            }

        }
    }

    /* Partners */

    .partners {
        margin-top: 10rem;

        .heading {
            @include global-heading();
            text-align: center;
            margin: 3rem 0;
            color: $clr06;
        }

        &__content {
            text-align: center;
            @include maxWidth();
            @include flexbox(space-between, center);
            flex-wrap: wrap;

            img {
                width: 300px;
                text-align: center;
                display: inline-block;
                height: auto;
            }
        }
    }

    /* Lets Talk */

    .letsTalk-about {
        margin-top: -6.5rem;
        background: $clr06;

        .link {
            @include button(#fff, #fff, transparent, 3px solid $clr04);

            &:hover {
                &:hover {
                    background: $clr04;
                    color: #fff;
                }
            }

        }
    }
}

/* Breakpoints */

@media screen and(max-width: 1000px) {
    .main-about {
        .thePack {
            .heading {
                font-size: 6rem;
            }

            &__content {
                background: transparent;
                color: #282828;

                &__flex {
                    margin-top: 0;
                    flex-wrap: wrap;

                    &__left-quote,
                    &__right-desc {
                        width: 100%;
                        @include flexbox(start, start);
                        height: auto;
                        color: #282828;
                        background: $clr02;
                        padding: 1.5rem 4rem;


                        h2,
                        span,
                        p {
                            width: 100%;
                            color: #fff;
                        }
                    }
                }
            }
        }
    }
}

@media screen and(max-width: 880px) {
    .main-about {
        .team-description {
            &__content-top {
                padding: 4rem 4rem;
            }

            &__content-bottom {
                grid-template-columns: 1fr;
                grid-template-areas: 'content-one''showcase-one''content-two''showcase-two';

                &__right-showcase-one,
                &__left-showcase-two {
                    img {
                        width: 100%;
                        height: 55vh;
                    }
                }

                &__left-content-one,
                &__right-content-two {
                    padding: 4rem 2rem;
                }

            }
        }


    }
}

@media screen and(max-width: 768px) {

    .header-about {
        .about-showcase {
            background: none;
            margin-top: -4rem;

            h2 {
                @include pack-heading();
                margin: 5rem 0;
                font-size: 4rem;
                display: block;
                text-align: center;
            }
        }
    }

    .main-about {

        .thePack {
            .heading {
                font-size: 4rem;
                margin: 4rem 0;
                text-align: center;
            }

            &__content {
                background: transparent;
                color: #282828;

                &__flex {
                    margin-top: 0;
                    flex-wrap: wrap;

                    &__left-quote,
                    &__right-desc {
                        width: 100%;
                        @include flexbox(start, start);
                        height: auto;
                        color: #282828;
                        background: $clr02;
                        padding: 1.5rem 2rem;


                        h2,
                        span,
                        p {
                            width: 100%;
                            color: #fff;
                        }
                    }
                }
            }
        }


        .letsTalk-about {
            clip-path: none;
            margin-top: 0;
        }
    }
}

@media screen and (max-width: 600px) {

    .main-about {
        .partners {
            margin-top: 5rem;

            &__content {
                @include flexbox(center, center);
                padding: 0 2rem;
            }
        }

        .letsTalk-about {
            clip-path: none;
            margin-top: 0;
        }

        .team-description {
            &__content-top {
                padding: 4rem 2rem;

                h1 {
                    font-size: 2rem;
                    line-height: 1.4;
                }
            }
        }
    }
}

@media screen and (max-width: 500px) {
    .main-about {
        .thePack {
            &__content {
                img {
                    width: 100%;
                    height: 320px;
                }
            }
        }
    }
}

