/* WWD MAIN CONTENT */

.main {
    padding-top: 1.5rem;

    .digitalDesign,
    .brandDesign,
    .experienceDesign {
        .heading {
            @include wwd-heading();
            text-align: center;
            margin: 5rem 0;
        }

        &__content {
            @include maxWidthInner();
            padding: 3rem 0;

            p:nth-child(1) {
                @include design-p(bold);
                margin-bottom: 1.5rem;
            }

            p:nth-child(2) {
                @include design-p(lighter);
            }
        }

        &__services {
            @include flexbox(space-between, center);
            @include maxWidthInner();
            text-align: center;
            padding-top: 3rem;

            img {
                display: inline-block;
                width: 90px;
                text-align: center;
                height: auto;
            }

            span {
                display: block;
                margin: .5rem 0;
                color: $clr07;
            }

            h2 {
                @include design-service-heading();
                line-height: 1.4;
            }
        }
    }

    /* WWD SHOWCASE */

    .brand-showcase {
        width: 100%;
        height: 35vh;
        background-size: cover;
        background-position: center center;
        margin-top: 5rem;

        &__content {
            @include flexbox(center, start);
            flex-direction: column;
            height: 100%;
            @include maxWidth();
            position: relative;

            .arrow-top {
                position: absolute;
