            .img1 {
                grid-area: img1;
            }

            .img2 {
                grid-area: img2;
            }

            .img3 {
                grid-area: img3;
            }
        }
    }

    .showcase-four {
        position: relative;

        &:before {
            content: '';
            position: absolute;
            left: 0;
            bottom: 0;
            width: 100%;
            height: 40vh;
            background: $project-bg7;
            z-index: 1;
        }

        img {
            @include projectWidth();
            margin: 0rem auto;
            display: block;
            position: relative;
            z-index: 4;
            margin-top: 5rem;
            width: 100%;
            height: auto;

        }


